* {
    box-sizing: border-box;
}

ul,
li {
    list-style: none;
    padding: 0;
}


/* ___________________________HEADER___________________________ */

@font-face {
    font-family: "Armour_W_XBd";
    src: url("../font/Armour XBold.ttf") format("woff");
}

@font-face {
    font-family: "Armour_W_Bd";
    src: url("../font/Armour Bold.ttf") format("woff");
}

@font-face {
    font-family: "Armour_W_Rg";
    src: url("../font/Armour Regular.ttf") format("woff");
}

body {
    background-color: #fff;
    color: #000;
    font-family: Arial, Helvetica, sans-serif;
}


/* main{
    width: 80%;
    margin-left: 10%;
} */

section {
    width: 100%;
}

.bloc_text h3 {
    font-family: Armour_W_XBd, Arial, sans-serif;
    font-size: 25px;
    margin: 10px 0 5px;
}

.bloc_text p {
    font-family: Armour_W_Rg, Arial, sans-serif;
    margin-top: 15px;
    font-size: 13px;
    line-height: 16px;
}

.bloc-meilleurs_vente {
    width: 100%;
    text-align: center;
    padding: 60px 0;
}

.bloc-meilleurs_vente h3 {
    width: 96%;
    display: block;
    margin: 0 2%;
    font-size: 5vw;
    font-size: 25px;
    color: #1d1d1d;
    font-weight: 400;
    line-height: 1;
}

.en-tete_section {
    width: 96%;
    display: block;
    margin: 0 2%;
    font-size: 5vw;
    font-size: 25px;
    color: #1d1d1d;
    font-family: Armour_W_Bd, Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: 1;
}

.titre {
    width: 96%;
    display: block;
    margin: 0 2%;
    padding: 0;
    font-size: 1.72vw;
    font-size: 45px;
    color: #1d1d1d;
    font-family: Armour_W_XBd, Arial, sans-serif;
    font-weight: 400;
    line-height: 1;
    text-transform: none;
}

.btn_ancre {
    transition: all .5s ease;
    display: inline-block;
    background-color: transparent;
    font-family: Armour_W_Bd, Arial, sans-serif;
    font-weight: 400;
    font-size: 13px;
    color: #1d1d1d;
    border: 2px solid #1d1d1d;
    padding: 11.5px 20px;
    margin: 10px 8px 0;
    border-radius: 4px;
}

.btn_ancre:hover {
    background-color: #000;
    transition: 0.5;
    color: #fff;
}


/* *, ::after,::before{
    box-sizing: inherit;
} */

.section_images {
    width: 100%;
    display: flex;
    justify-content: space-around;
}

.bloc_img {
    width: 100%;
    height: auto;
    position: relative;
    cursor: pointer;
}

.container_img {
    position: relative;
    width: auto;
    height: auto;
    padding: 0px 5px;
    transform: translate(0, 0);
}

.image:hover {
    filter: opacity(50%);
    -webkit-filter: brightness(50%);
}

.image {
    max-width: 100%;
    height: auto;
    margin: 0;
}

.texte_img-h3 {
    font-size: 25px;
    font-weight: bolder;
    font-family: "Arial black", sans-serif;
}

.texte_img-h4 {
    font-size: 12px;
    font-weight: lighter;
}

.bloc_text {
    text-align: center;
    justify-content: space-around;
    margin: 40px auto 0;
    max-width: 450px;
    width: 90%;
}

.btn_acheter_articles--desktop {
    background: 0 0;
    color: #fff;
    border: 2px solid #fff;
    border-radius: 4px;
    padding: 11.5px 20px;
    position: absolute;
    font-size: 13px;
    font-family: Armour_W_Bd, Arial, sans-serif;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 1;
    letter-spacing: -0.2px;
    font-weight: bolder;
    opacity: 0;
    /*opcacity button set to 0*/
    cursor: pointer;
    transition: all .5s ease;
}


/* Image hover show button */

.container_img:hover .btn_acheter_articles--desktop {
    opacity: 1;
}


/* Image hover show button */

.container_btn--mobile {
    text-align: center;
}

.btn_acheter_articles--mobile {
    display: inline-block;
    font-family: Armour_W_Bd, Arial, sans-serif;
    font-weight: 400;
    color: #1d1d1d;
    background-color: #fff;
    border: 2px solid #1d1d1d;
    padding: 7px 10px;
    font-size: 12px;
    margin-top: 10px;
    margin-bottom: 30px;
    transition: all .5s ease;
    border-radius: 4px;
    cursor: pointer;
}

.btn_acheter_articles--desktop:hover {
    color: #000;
    background-color: #fff;
}


/* Image hover show background */

.overlay {
    position: absolute;
    top: 0;
    left: 5px;
    width: 99%;
    height: 100%;
    background: rgba(0, 0, 0, 0);
    transition: background 0.5s ease;
}

.container_img:hover .overlay {
    display: block;
    background: rgba(0, 0, 0, .3);
}


/* Image hover show background */

@media screen and (max-width: 767px) {
    .bloc-meilleurs_vente.titre {
        margin: 10px 50px 20px;
    }
    .en-tete_section {
        font-size: 10px;
    }
    .titre {
        font-size: 28px;
    }
    .btn_ancre {
        background-color: #fff;
        color: #000;
    }
    .section_images {
        width: 100%;
        display: flex;
        flex-flow: column wrap;
    }
    .bloc_text {
        width: 100%;
        display: flex;
        flex-flow: column wrap;
    }
    .bloc_text h3 {
        font-family: Armour_W_XBd, Arial, sans-serif;
        font-size: 12px;
        margin: 10px 0 0;
    }
    .bloc_img {
        width: 100%;
    }
    .btn_acheter_articles--desktop {
        display: none;
    }
}

@media screen and (min-width: 768px) {
    .container_btn--mobile {
        display: none;
    }
}

@media (max-width: 991px) {
    .bloc_text h3 {
        font-size: 17px;
    }
    .bloc_text p {
        font-size: 14px;
    }
    .overlay {
        left: 4px;
        width: 98%;
    }
}

@media (max-width: 1199px) {
    .bloc_text h3 {
        margin-top: 3px;
    }
    .bloc_text p {
        font-family: Armour_W_Rg, Arial, sans-serif;
        margin-top: 15px;
        font-size: 13px;
        line-height: 16px;
    }
    .overlay {
        left: 5px;
        width: 98%;
        height: 99%;
    }
}


/* DEUXIEME PARTIE */


